(function () {
	"use strict";

	var CONSTANTS = require('./constants.js');
	var Element = require('./element.js');

	function DocumentFragment () {
		var element = new Element('', CONSTANTS.DOCUMENT_FRAGMENT_NODE);
		for (var key in element) {
			if(!element.hasOwnProperty(key))
				continue;
			Object.defineProperty(this, key, Object.getOwnPropertyDescriptor(element, key));
		}
	}

	for (var key in Element.prototype)
		Object.defineProperty(DocumentFragment.prototype, key, Object.getOwnPropertyDescriptor(Element.prototype, key));

	Object.defineProperty(DocumentFragment.prototype, 'toString', {value: function () {
		var strs = [];
		for (var i = 0, l = this.childNodes.length; i < l; ++i)
			strs.push(this.childNodes[i].toString());
		return strs.join('\n');
	}});

	module.exports = DocumentFragment;
})()