(function () {
	/*need for handlemort only*/
	"use strict";
	function mean () {
		var sum = 0,
		    length = arguments.length;
		for (var i = 0; i < length; ++i)
			sum += arguments[i];
		return sum / length;
	}
	module.exports = function inttervalCenter (str) {
		str = str.split('-');
		return mean(+str[0], +(str[1] || str[0]) + 1);
	}
})()