(function () {
	"use strict";

	/*Move to mortality modules*/

	var isNumeric = require('../index.js').isNumeric;

	module.exports = exports.ratesByAge = function ratesByAge (obj) {
		var res = [];
		for (var key in obj) {
			if (isNumeric(key))
				res.push({x: 0.5 + (+key), y: isNumeric(obj[key]) ? +obj[key] : 0});
			else if(key.match(/^\d+-?\d*$/)) {
				var tmp = key.split('-');
				res.push({x: (tmp.reduce(function (x, y) {return (+x) + (+y);}, 1))/ tmp.length, y: isNumeric(obj[key]) ? +obj[key] : 0});
			}
		}
		return res;
	}
})()