(function () {
	"use strict";
	function round (value, to) {return Math.round(value * Math.pow(10, -Math.log(to) / Math.log(10))) * to;}
	module.exports = function roundFields (values, positions) {
		for (var key in values) {
			var tmp = round(values[key], positions[key] || 1);
			if (tmp)
				values[key] = tmp;
		}
		return values;
	}
})()